use std::collections::HashSet;

fn solve(arg: &str, num: usize) -> i32 {
    let mut tmp: Vec<Vec<char>> = vec![];
    for (i, c) in arg.chars().enumerate() {
        let mut chs: Vec<char> = vec![];
        if let Some(last) = tmp.last() {
            let len = last.len();
            if len < num {
                for last_char in last.iter() {
                    chs.push(*last_char);
                }
                chs.push(c);
            } else if len == num {
                let set = last.iter().collect::<HashSet<_>>();
                if last.len() == set.len() {
                    return i as i32;
                }
                for last_char in last.iter().skip(1) {
                    chs.push(*last_char);
                }
                chs.push(c);
            }
        } else {
            chs.push(c);
        }
        tmp.push(chs);
    }
    -1
}

fn part1(arg: &str) -> i32 {
    solve(arg, 4)
}

fn part2(arg: &str) -> i32 {
    solve(arg, 14)
}

fn main() {
    println!("{}", part1(""));
    println!("{}", part2(""));
}
